import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import StudentForm from '../components/StudentForm';
import './AddStudent.css'; // Thêm file CSS tùy chỉnh cho trang này

function AddStudent() {
  const [formData, setFormData] = useState({
    name: '',
    class: '',
    studentId: '',
    dob: '',
    placeOfBirth: '',
    faculty: '',
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(''); // Reset error message

    try {
      const response = await axios.post('http://localhost:5000/students', formData);
      if (response.status === 200) {
        navigate('/dashboard');
      }
    } catch (error) {
      setError('Failed to add student. Please try again later.');
      console.error('Error adding student:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="add-student-container">
      <div className="add-student-form">
        <h1>Add New Student</h1>
        {error && <div className="error-message">{error}</div>} {/* Hiển thị thông báo lỗi nếu có */}
        <StudentForm
          formData={formData}
          setFormData={setFormData}
          handleSubmit={handleSubmit}
          buttonLabel={isSubmitting ? 'Submitting...' : 'Add Student'}
        />
      </div>
    </div>
  );
}

export default AddStudent;
