import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './StudentDetail.css';

function StudentDetail() {
  const { studentId } = useParams(); // Lấy studentId từ URL
  const [student, setStudent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStudentDetails = async () => {
      try {
        setLoading(true);
        setError(null); // Reset lỗi trước khi bắt đầu
        const response = await axios.get(`http://localhost:5000/students/studentId/${studentId}`);
        setStudent(response.data);
      } catch (error) {
        console.error('Error fetching student details:', error);
        setError(`Failed to fetch student details: ${error.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchStudentDetails();
  }, [studentId]); // Lấy dữ liệu lại khi studentId thay đổi

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error-message">{`Error: ${error}`}</div>;
  }

  if (!student) {
    return <div className="error-message">Student not found</div>;
  }

  return (
    <div className="student-detail-container">
      <h1>Student Details</h1>
      <div className="student-info">
        <img src={student.imageUrl || 'default-image.jpg'} alt={student.name} className="student-image" />
        <div className="student-details">
          <p><strong>Name:</strong> {student.name}</p>
          <p><strong>Student ID:</strong> {student.studentId}</p>
          <p><strong>Class:</strong> {student.class}</p>
          <p><strong>Date of Birth:</strong> {student.dob}</p>
          <p><strong>Place of Birth:</strong> {student.placeOfBirth}</p>
          <p><strong>Faculty:</strong> {student.faculty}</p>
        </div>
      </div>
    </div>
  );
}

export default StudentDetail;
