import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function AddStudent() {
  const [formData, setFormData] = useState({
    name: '',
    class: '',
    studentId: '',
    dob: '',
    placeOfBirth: '',
    faculty: '',
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/students', formData);
      navigate('/dashboard');
    } catch (error) {
      console.error('Error adding student:', error);
    }
  };

  return (
    <div>
      <h1>Add Student</h1>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Name" onChange={handleChange} required />
        <input name="class" placeholder="Class" onChange={handleChange} required />
        <input name="studentId" placeholder="Student ID" onChange={handleChange} required />
        <input name="dob" placeholder="Date of Birth" type="date" onChange={handleChange} required />
        <input name="placeOfBirth" placeholder="Place of Birth" onChange={handleChange} required />
        <input name="faculty" placeholder="Faculty" onChange={handleChange} required />
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default AddStudent;
