import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './AddStudent.css'; // Import CSS tùy chỉnh

function AddStudent() {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Xử lý khi form được submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(''); // Reset lỗi

    try {
      const response = await axios.post('http://localhost:5000/students', formData);
      if (response.status === 200) {
        navigate('/dashboard'); // Chuyển hướng sau khi thành công
      }
    } catch (error) {
      setError('Failed to add student. Please try again later.');
      console.error('Error adding student:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  // Xử lý thay đổi form input
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  return (
    <div className="add-student-container">
      <div className="add-student-form">
        <h1>Add New Student</h1>
        {error && <div className="error-message">{error}</div>} {/* Hiển thị thông báo lỗi nếu có */}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter student name"
              required
            />
            <label>Student Name</label>
          </div>
          <div className="form-group">
            <input
              type="text"
              name="class"
              value={formData.class}
              onChange={handleChange}
              placeholder="Enter class"
              required
            />
            <label>Class</label>
          </div>
          <div className="form-group">
            <input
              type="text"
              name="studentId"
              value={formData.studentId}
              onChange={handleChange}
              placeholder="Enter student ID"
              required
            />
            <label>Student ID</label>
          </div>
          <div className="form-group">
            <input
              type="date"
              name="dob"
              value={formData.dob}
              onChange={handleChange}
              required
            />
            <label>Date of Birth</label>
          </div>
          <div className="form-group">
            <input
              type="text"
              name="placeOfBirth"
              value={formData.placeOfBirth}
              onChange={handleChange}
              placeholder="Enter place of birth"
              required
            />
            <label>Place of Birth</label>
          </div>
          <div className="form-group">
            <input
              type="text"
              name="faculty"
              value={formData.faculty}
              onChange={handleChange}
              placeholder="Enter faculty"
              required
            />
            <label>Faculty</label>
          </div>
          <button type="submit" disabled={isSubmitting}>
            {isSubmitting ? 'Submitting...' : 'Add Student'}
          </button>
        </form>
      </div>
    </div>
  );
}

export default AddStudent;
