import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { format } from 'date-fns'; // Import format từ date-fns
import './Dashboard.css';

function Dashboard() {
  const [students, setStudents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/students');
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const handleAdd = () => navigate('/add');
  const handleEdit = (id) => navigate(`/edit/${id}`);
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/students/${id}`);
      fetchStudents();
    } catch (error) {
      console.error('Error deleting student:', error);
    }
  };

  const handleLogout = () => {
    // Xử lý logout tại đây (xóa token, reset trạng thái người dùng...)
    navigate('/login');
  };

  return (
    <div className="dashboard-container">
      {/* Header section with logout and back to login */}
      <div className="header">
        <h1 className="dashboard-header">Student Dashboard</h1>
        <div className="header-actions">
          <button className="logout-button" onClick={handleLogout}>Logout</button>
          <button className="back-to-login-button" onClick={() => navigate('/login')}>Back to Login</button>
        </div>
      </div>

      {/* Table of students */}
      <table className="students-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Class</th>
            <th>Date of Birth</th>
            <th>Place of Birth</th>
            <th>Faculty</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student._id}>
              <td>{student.name}</td>
              <td>{student.class}</td>
              <td>{format(new Date(student.dob), 'dd/MM/yyyy')}</td> {/* Định dạng lại ngày sinh */}
              <td>{student.placeOfBirth}</td>
              <td>{student.faculty}</td>
              <td>
                <button className="edit-button" onClick={() => handleEdit(student._id)}>Edit</button>
                <button className="delete-button" onClick={() => handleDelete(student._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Dashboard;
