import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import moment from 'moment';
import './StudentDetail.css'; // Định dạng trang chi tiết sinh viên

function StudentDetail() {
  const { id } = useParams(); // Lấy id từ URL
  const [student, setStudent] = useState(null);

  useEffect(() => {
    fetchStudentDetail();
  }, [id]);

  const fetchStudentDetail = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/students/${id}`);
      setStudent(response.data);
    } catch (error) {
      console.error('Error fetching student details:', error);
    }
  };

  if (!student) {
    return <div>Loading...</div>; // Hiển thị khi dữ liệu đang tải
  }

  return (
    <div className="student-detail-container">
      <h1>Student Details</h1>
      <div className="student-detail">
        <img src={student.photo || '/default-avatar.jpg'} alt="Student" className="student-photo" /> {/* Hiển thị ảnh sinh viên */}
        <div className="student-info">
          <p><strong>Name:</strong> {student.name}</p>
          <p><strong>Student ID:</strong> {student.studentId}</p>
          <p><strong>Class:</strong> {student.class}</p>
          <p><strong>Date of Birth:</strong> {moment(student.dob).format('DD/MM/YYYY')}</p>
          <p><strong>Place of Birth:</strong> {student.placeOfBirth}</p>
          <p><strong>Faculty:</strong> {student.faculty}</p>
        </div>
      </div>
    </div>
  );
}

export default StudentDetail;
