import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './StudentDetail.css';

function StudentDetail() {
  const { id } = useParams();  // Lấy ID sinh viên từ URL
  const [student, setStudent] = useState(null);  // Dữ liệu sinh viên
  const [loading, setLoading] = useState(true);  // Trạng thái tải dữ liệu
  const [error, setError] = useState(null);  // Trạng thái lỗi
  const navigate = useNavigate();

  // Lấy chi tiết sinh viên khi component được render
  useEffect(() => {
    const fetchStudentDetails = async () => {
      try {
        setLoading(true);  // Bắt đầu tải dữ liệu
        setError(null);     // Reset lỗi trước khi lấy dữ liệu
        const response = await axios.get(`http://localhost:5000/students/${id}`);
        setStudent(response.data);
      } catch (error) {
        console.error('Error fetching student details:', error);
        setError('Failed to fetch student details. Please try again later.');
      } finally {
        setLoading(false);  // Hoàn thành tải dữ liệu
      }
    };

    fetchStudentDetails();
  }, [id]);

  // Nếu dữ liệu đang tải, hiển thị Loading
  if (loading) {
    return <div className="loading-message">Loading...</div>;
  }

  // Nếu có lỗi khi tải dữ liệu, hiển thị thông báo lỗi
  if (error) {
    return <div className="error-message">{error}</div>;
  }

  // Nếu không có dữ liệu sinh viên
  if (!student) {
    return <div className="error-message">No student found.</div>;
  }

  return (
    <div className="student-detail-container">
      <h1 className="student-detail-header">Student Detail</h1>
      <div className="student-info">
        {/* Hiển thị ảnh sinh viên */}
        <img
          src={student.imageUrl || 'default-image-url.jpg'}  // Dùng ảnh mặc định nếu không có ảnh
          alt={student.name}
          className="student-image"
        />
        <div className="student-details">
          <span>Name:</span> <p>{student.name}</p>
          <span>Student ID:</span> <p>{student.studentId}</p>
          <span>Class:</span> <p>{student.class}</p>
          <span>Date of Birth:</span> <p>{student.dob}</p>
          <span>Place of Birth:</span> <p>{student.placeOfBirth}</p>
          <span>Faculty:</span> <p>{student.faculty}</p>
        </div>
      </div>
      <button className="back-to-dashboard" onClick={() => navigate('/dashboard')}>
        Back to Dashboard
      </button>
    </div>
  );
}

export default StudentDetail;
